import React, { useEffect, useState } from 'react';
import {
  checkin,
  checkout,
  getHistory,
  getLeaveHistory,
  submitLeaveRequest,
  getEmployeeSummary,
  getProfile,
  updateEmployeeProfile
} from '../services/api';
import { useNavigate } from 'react-router-dom';
import './EmployeeDashboard.css';
import ClockGreeting from '../components/ClockGreeting';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Modal Components
import ProfileModal from '../components/ProfileModal';
import SettingsModal from '../components/SettingsModal';

const EmployeeDashboard = () => {
  const [history, setHistory] = useState([]);
  const [checkedInToday, setCheckedInToday] = useState(false);
  const [leaveHistory, setLeaveHistory] = useState([]);
  const [leaveDate, setLeaveDate] = useState('');
  const [reason, setReason] = useState('');
  const [activeTab, setActiveTab] = useState('attendance');
  const [showCheckinModal, setShowCheckinModal] = useState(false);
  const [showCheckoutModal, setShowCheckoutModal] = useState(false);
  const [attendanceDateTime, setAttendanceDateTime] = useState('');

  const [summary, setSummary] = useState({
    totalDays: 0,
    leavesTaken: 0,
    attendancePercent: 0,
    pendingRequests: 0,
  });
  const [employee, setEmployee] = useState({
    name: '',
    email: '',
    department: '',
  });
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);

  const navigate = useNavigate();
  const leavesLeft = 20 - summary.leavesTaken;

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    await fetchHistory();
    await fetchLeaveHistory();
    await fetchSummary();
    await fetchEmployeeProfile();
  };

  const fetchEmployeeProfile = async () => {
    try {
      const res = await getProfile();
      setEmployee(res.data);
    } catch (err) {
      console.error('Failed to fetch profile:', err);
      toast.error('Failed to load profile');
    }
  };

  const fetchSummary = async () => {
    try {
      const res = await getEmployeeSummary();
      setSummary(res.data);
    } catch (err) {
      console.error('Failed to fetch summary:', err);
      toast.error('Failed to load dashboard data');
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await getHistory();
      setHistory(res.data);

      const today = new Date().toISOString().split('T')[0];
      const todayLog = res.data.find((log) => log.date === today);

      const hasIncompletePrevious = res.data.some(
        (log) => log.date !== today && log.checkin && !log.checkout
      );

      if (hasIncompletePrevious) {
        setCheckedInToday('needs_checkout');
        toast.warning('You have incomplete check-out from previous day');
      } else if (todayLog?.checkin && !todayLog?.checkout) {
        setCheckedInToday(true);
      } else if (todayLog?.checkin && todayLog?.checkout) {
        setCheckedInToday('done');
      } else {
        setCheckedInToday(false);
      }
    } catch (err) {
      console.error(err);
      toast.error('Failed to load attendance history');
    }
  };

  const fetchLeaveHistory = async () => {
    try {
      const res = await getLeaveHistory();
      setLeaveHistory(res.data);
    } catch (err) {
      console.error('Failed to fetch leave history', err);
      toast.error('Failed to load leave history');
    }
  };

  const handleCheckIn = async () => {
    try {
      await checkin();
      fetchHistory();
      toast.success('Checked in successfully!');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Check-in failed!');
    }
  };

  const handleCheckOut = async () => {
    try {
      await checkout();
      fetchHistory();
      toast.success('Checked out successfully!');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Check-out failed!');
    }
  };

  const handleLeaveSubmit = async (e) => {
    e.preventDefault();
    if (!leaveDate || !reason) {
      toast.warning('Please fill out both fields');
      return;
    }
    try {
      await submitLeaveRequest({ date: leaveDate, reason });
      toast.success('Leave request submitted');
      setLeaveDate('');
      setReason('');
      fetchLeaveHistory();
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Leave submission failed');
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  const ProfileAvatar = ({ name, department }) => {
    const initials = name.split(' ').map(part => part[0]).join('').toUpperCase();

    const departmentColors = {
      HR: '#FF6B6B',
      Engineering: '#4ECDC4',
      Marketing: '#FFD166',
      Sales: '#06D6A0',
      Default: '#A5A5A5'
    };

    return (
      <div
        className="profile-avatar"
        style={{
          backgroundColor: departmentColors[department] || departmentColors.Default,
          cursor: 'pointer'
        }}
        onClick={() => setShowProfileDropdown(!showProfileDropdown)}
      >
        {initials}
      </div>
    );
  };


  const handleSettingsSave = async () => {
    try {
      await updateEmployeeProfile({
        name: employee.name,
        department: employee.department,
        position: employee.position,
      });
      toast.success("Profile updated successfully!");
      setShowSettingsModal(false);
    } catch (error) {
      toast.error("Failed to update profile");
      console.error(error);
    }
  };

  const holidays = [
    { name: 'May Day', date: '2025-05-01' },
    { name: 'Independence Day', date: '2025-08-15' },
    { name: 'Ganesh Chaturthi', date: '2025-08-27' },
    { name: 'Gandhi Jayanti', date: '2025-10-02' },
    { name: 'Diwali', date: '2025-10-20' },
    { name: 'Christmas', date: '2025-12-25' }
  ];

  const today = new Date();
  const nextHoliday = holidays.find(holiday => new Date(holiday.date) >= today);

  return (
    <div className="emp-container">
      <div className="emp-card">
        {/* Header */}
        <div className="emp-header">
          <div className="emp-title">
            <img src="/Otomeyt_AI_Logo.jpg" alt="Otomeyt Logo" className="logo" />
            <h1>Employee Dashboard</h1>
          </div>

          <div className="profile-section">
            <div className="profile-info">
              <span className="profile-name">{employee.name}</span>
              <span className="profile-dept">{employee.department}</span>
            </div>
            <ProfileAvatar name={employee.name} department={employee.department} />
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <div className="dropdown-header">
                  <ProfileAvatar name={employee.name} department={employee.department} size="large" />
                  <div>
                    <div className="dropdown-name">{employee.name}</div>
                    <div className="dropdown-email">{employee.email}</div>
                  </div>
                </div>
                <div className="dropdown-divider"></div>
                <div className="dropdown-item" onClick={() => setShowProfileModal(true)}>
                  <i className="icon-user"></i> View Profile
                </div>
                <div className="dropdown-item" onClick={() => setShowSettingsModal(true)}>
                  <i className="icon-settings"></i> Settings
                </div>
                <div className="dropdown-divider"></div>
                <div className="dropdown-item" onClick={handleLogout}>
                  <i className="icon-logout"></i> Logout
                </div>
              </div>
            )}
          </div>
        </div>

        <ClockGreeting employeeName={employee.name.split(' ')[0]} />

        {/* Summary Cards */}
        <div className="summary-cards">
          <div className="card"><h4>Upcoming Holiday</h4> <p>{nextHoliday ? ` ${nextHoliday.date}` : 'No upcoming holidays'}</p></div>
          <div className="card"><h4>Leaves Taken</h4><p>{summary.leavesTaken}</p></div>
          <div className="card"><h4>Leaves Left</h4><p>{leavesLeft}</p></div>
          <div className="card"><h4>Pending Requests</h4><p>{summary.pendingRequests}</p></div>
        </div>

        {/* Tab Navigation */}
        <div className="tab-group">
          <button className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</button>
          <button className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Request</button>
          <button className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</button>
        </div>

        {/* Attendance Tab */}
        {activeTab === 'attendance' && (
          <>
            <div className="btn-group">
  <button onClick={() => setShowCheckinModal(true)}>Check In</button>
  <button onClick={() => setShowCheckoutModal(true)}>Check Out</button>
  {/* Check-in Modal */}
{showCheckinModal && (
  <div className="modal-overlay">
    <div className="modal-content">
      <h3>Select Date and Time for Check-In</h3>
      <input
        type="datetime-local"
        value={attendanceDateTime}
        onChange={(e) => setAttendanceDateTime(e.target.value)}
      />
      <div className="modal-buttons">
        <button onClick={async () => {
          try {
            await checkin({ datetime: attendanceDateTime });
            toast.success("Checked in successfully!");
            fetchHistory();
            setShowCheckinModal(false);
            setAttendanceDateTime('');
          } catch (err) {
            toast.error("Check-in failed");
          }
        }}>Submit</button>
        <button className="modal-close" onClick={() => setShowCheckinModal(false)}>Cancel</button>
      </div>
    </div>
  </div>
)}

{/* Check-out Modal */}
{showCheckoutModal && (
  <div className="modal-overlay">
    <div className="modal-content">
      <h3>Select Date and Time for Check-Out</h3>
      <input
        type="datetime-local"
        value={attendanceDateTime}
        onChange={(e) => setAttendanceDateTime(e.target.value)}
      />
      <div className="modal-buttons">
        <button onClick={async () => {
          try {
            await checkout({ datetime: attendanceDateTime });
            toast.success("Checked out successfully!");
            fetchHistory();
            setShowCheckoutModal(false);
            setAttendanceDateTime('');
          } catch (err) {
            toast.error("Check-out failed");
          }
        }}>Submit</button>
        <button className="modal-close" onClick={() => setShowCheckoutModal(false)}>Cancel</button>
      </div>
    </div>
  </div>
)}

</div>


            <div className="history">
              <h3>Attendance History</h3>
              <table>
                <thead>
                  <tr><th>Date</th><th>Check-In</th><th>Check-Out</th></tr>
                </thead>
                <tbody>
                  {history.map((log, i) => (
                    <tr key={i}>
                      <td>{log.date}</td>
                      <td>{log.checkin || '—'}</td>
                      <td>{log.checkout || '—'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {/* Leave Request Tab */}
        {activeTab === 'leave' && (
          <>
            <div className="leave-request">
              <h3>Request Leave</h3>
              <form onSubmit={handleLeaveSubmit}>
                <input type="date" value={leaveDate} onChange={(e) => setLeaveDate(e.target.value)} min={new Date().toISOString().split("T")[0]} required />
                <textarea placeholder="Reason for leave" value={reason} onChange={(e) => setReason(e.target.value)} required></textarea>
                <button type="submit">Submit Leave Request</button>
              </form>
            </div>

            <div className="leave-history">
              <h3>Leave History</h3>
              <table>
                <thead>
                  <tr><th>Date</th><th>Reason</th><th>Status</th></tr>
                </thead>
                <tbody>
                  {leaveHistory.map((req, i) => (
                    <tr key={i}>
                      <td>{req.date}</td>
                      <td>{req.reason}</td>
                      <td>{req.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {/* Holiday Calendar Tab */}
        {activeTab === 'holiday' && (
          <div className="holiday-calendar">
            <h3>Holiday Calendar - 2025</h3>
            <table>
              <thead><tr><th>Date</th><th>Day</th><th>Holiday</th></tr></thead>
              <tbody>
                <tr><td>01-Jan-25</td><td>Wednesday</td><td>New Year</td></tr>
                <tr><td>14-Jan-25</td><td>Tuesday</td><td>Makara Sankranti</td></tr>
                <tr><td>26-Feb-25</td><td>Wednesday</td><td>Maha Shivaratri</td></tr>
                <tr><td>31-Mar-25</td><td>Monday</td><td>Qutub-E-Ramzan</td></tr>
                <tr><td>01-May-25</td><td>Thursday</td><td>May Day</td></tr>
                <tr><td>15-Aug-25</td><td>Friday</td><td>Independence Day</td></tr>
                <tr><td>27-Aug-25</td><td>Wednesday</td><td>Ganesh Chaturthi</td></tr>
                <tr><td>01-Oct-25</td><td>Wednesday</td><td>Mahanavami</td></tr>
                <tr><td>02-Oct-25</td><td>Thursday</td><td>Gandhi Jayanti</td></tr>
                <tr><td>20-Oct-25</td><td>Monday</td><td>Naraka Chaturdashi</td></tr>
                <tr><td>25-Dec-25</td><td>Thursday</td><td>Christmas</td></tr>
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Profile Modal */}
      {showProfileModal && <ProfileModal employee={employee} onClose={() => setShowProfileModal(false)} />}
      {showSettingsModal && <SettingsModal employee={employee} setEmployee={setEmployee} onClose={() => setShowSettingsModal(false)} />}
      <ToastContainer />
    </div>
  );
};

export default EmployeeDashboard;
