import React from 'react';
import './Sidebar.css';
import { FaTachometerAlt, FaUser, FaCalendarAlt, FaSignOutAlt } from 'react-icons/fa';

const Sidebar = ({ activeTab, onTabChange }) => {
  return (
    <aside className="sidebar">
      <div className="brand">Employee Panel</div>
      <ul>
        <li className={activeTab === 'dashboard' ? 'active' : ''} onClick={() => onTabChange('dashboard')}>
          <FaTachometerAlt /> Dashboard
        </li>
        <li className={activeTab === 'attendance' ? 'active' : ''} onClick={() => onTabChange('attendance')}>
          <FaCalendarAlt /> Attendance
        </li>
        <li className={activeTab === 'leave' ? 'active' : ''} onClick={() => onTabChange('leave')}>
          <FaCalendarAlt /> Leave Request
        </li>
        <li className={activeTab === 'profile' ? 'active' : ''} onClick={() => onTabChange('profile')}>
          <FaUser /> Profile
        </li>
        <li onClick={() => onTabChange('logout')}>
          <FaSignOutAlt /> Logout
        </li>
      </ul>
    </aside>
  );
};

export default Sidebar;
