// ========================================THIS IS FOR SELECT ALL OPTION=======================

import React, { useEffect, useState, useRef } from 'react';
import { PieChart, Pie, Cell, Tooltip, Legend } from 'recharts';
import {
  getAllRecords,
  exportCSV,
  getAllLeaveRequests,
  updateLeaveStatus,
  getPendingCheckins,
  approveCheckin,
  addEmployee
} from '../services/api';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; 
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [records, setRecords] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [filters, setFilters] = useState({ email: '', date: '' });
  const [activeTab, setActiveTab] = useState('attendance');
  const [pendingCheckins, setPendingCheckins] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [recordsPerPage] = useState(5);
  const [attendanceStats, setAttendanceStats] = useState({ present: 0, leave: 0, absent: 0 });
  const [totalEmployees, setTotalEmployees] = useState(0);
  const [newEmployee, setNewEmployee] = useState({ name: '', email: '', doj: '', password: '', department: '', position:'', });

  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const COLORS = ['#00C49F', '#FF8042', '#FFBB28'];

  useEffect(() => {
    fetchAllData();
  }, []);

  useEffect(() => {
    if (totalEmployees > 0) calculateAttendance();
  }, [records, leaveRequests, totalEmployees]);

  const fetchAllData = async () => {
    await fetchRecords();
    await fetchLeaveRequests();
    await fetchPendingCheckins();
    await fetchTotalEmployees();
  };

  const fetchRecords = async () => {
    try {
      const res = await getAllRecords(filters);
      setRecords(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchLeaveRequests = async () => {
    try {
      const res = await getAllLeaveRequests();
      setLeaveRequests(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchPendingCheckins = async () => {
    try {
      const res = await getPendingCheckins();
      setPendingCheckins(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchTotalEmployees = async () => {
    try {
      const res = await fetch('http://localhost:5000/admin/total-employees', {
        method: 'GET',
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      const data = await res.json();
      setTotalEmployees(data.total_employees);
    } catch (err) {
      console.error('Failed to fetch total employees:', err);
    }
  };

  const calculateAttendance = () => {
    const today = new Date().toISOString().split('T')[0];
    const present = records.filter(r => r.date === today && r.checkin).length;
    const leave = leaveRequests.filter(l => l.date === today).length;
    const absent = Math.max(0, totalEmployees - (present + leave));
    setAttendanceStats({ present, leave, absent });
  };

  const handleCheckinDecision = async (id, status) => {
    try {
      await approveCheckin(id, status);
      fetchPendingCheckins();
      fetchRecords();
    } catch (err) {
      console.error("Failed to update check-in:", err);
    }
  };

  const handleExport = async () => {
    try {
      const queryParts = [];
      if (filters.email.trim() !== '') queryParts.push(`email=${encodeURIComponent(filters.email.trim())}`);
      if (filters.date.trim() !== '') queryParts.push(`date=${encodeURIComponent(filters.date.trim())}`);
      const query = queryParts.length ? `?${queryParts.join('&')}` : '';
      const res = await fetch(`http://localhost:5000/admin/export${query}`, {
        method: 'GET',
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      if (res.status === 404) return toast.error("No matching records found to export!");
      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'attendance_export.csv');
      document.body.appendChild(link);
      link.click();
      toast.success("CSV exported successfully!");
    } catch (err) {
      console.error(err);
      toast.error("Failed to export attendance!");
    }
  };

  const handleLeaveDecision = async (id, status) => {
    try {
      await updateLeaveStatus(id, status);
      fetchLeaveRequests();
    } catch (err) {
      console.error('Failed to update leave status:', err);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  const handleUploadAttendance = async () => {
    if (!fileInputRef.current?.files[0]) return toast.error('Please select a file first!');
    const formData = new FormData();
    formData.append('file', fileInputRef.current.files[0]);
    try {
      await fetch('http://localhost:5000/admin/upload-attendance', {
        method: 'POST',
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        body: formData
      });
      toast.success('Attendance uploaded successfully!');
      fetchRecords();
    } catch (err) {
      console.error(err);
      toast.error('Failed to upload attendance.');
    }
  };

  const handleAddEmployee = async () => {
    try {
      if (!newEmployee.name || !newEmployee.email || !newEmployee.doj || !newEmployee.password) {
        toast.warning("Please fill out all fields.");
        return;
      }
      await addEmployee(newEmployee);
      toast.success("Employee added successfully!");
      setNewEmployee({ name: '', email: '', doj: '', password: '' });
    } catch (err) {
      console.error("Error adding employee:", err);
      toast.error("Failed to add employee.");
    }
  };

  const currentRecords = records.slice((currentPage - 1) * recordsPerPage, currentPage * recordsPerPage);
  const totalPages = Math.ceil(records.length / recordsPerPage);

  const attendanceData = [
        { name: 'Present', value: attendanceStats.present },
        { name: 'On Leave', value: attendanceStats.leave },
        { name: 'Absent', value: attendanceStats.absent }
      ];

  return (
    <div className="admin-container">
      <div className="admin-card">
        <div className="admin-header-flex">
          <div className="admin-logo-title">
            <img src="/Otomeyt_AI_Logo.jpg" alt="Otomeyt Logo" />
            <h2>Admin Dashboard</h2>
          </div>
          <ToastContainer position="top-right" autoClose={3000} theme="colored" />
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>

        <div className="tab-switch">
          <button className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</button>
          <button className={activeTab === 'pending' ? 'active' : ''} onClick={() => setActiveTab('pending')}>Pending Check-ins</button>
          <button className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Requests</button>
          <button className={activeTab === 'upload' ? 'active' : ''} onClick={() => setActiveTab('upload')}>Upload Attendance</button>
          <button className={activeTab === 'add' ? 'active' : ''} onClick={() => setActiveTab('add')}>Add Employee</button>
        </div>

        {activeTab === 'attendance' && (
          <>
            <div className="filter-group">
              <input type="email" placeholder="Filter by Email" value={filters.email} onChange={(e) => setFilters({ ...filters, email: e.target.value })} />
              <input type="date" value={filters.date} onChange={(e) => setFilters({ ...filters, date: e.target.value })} />
              <button onClick={fetchRecords}>Search</button>
              <button onClick={handleExport}>Export CSV</button>
            </div>

            <div className="admin-graph">
              <h3>Attendance Overview</h3>
              <PieChart width={300} height={300}>
                <Pie data={attendanceData} cx="50%" cy="50%" outerRadius={100} label dataKey="value">
                  {attendanceData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </div>

            <div className="admin-records">
              <h3>Attendance Records</h3>
              <table>
                <thead>
                  <tr><th>Email</th><th>Date</th><th>Check-In</th><th>Check-Out</th></tr>
                </thead>
                <tbody>
                  {currentRecords.map((record, i) => (
                    <tr key={i}>
                      <td>{record.email}</td>
                      <td>{record.date}</td>
                      <td>{record.checkin || '—'}</td>
                      <td>{record.checkout || '—'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="pagination">
                <button onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}>Previous</button>
                <span>{currentPage}</span>
                <button onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}>Next</button>
              </div>
            </div>
          </>
        )}

        {activeTab === 'pending' && (
          <div className="pending-checkins">
            <h3>Pending Check-In Approvals</h3>
            <table>
              <thead>
                <tr><th>Email</th><th>Date</th><th>Check-In Time</th><th>Actions</th></tr>
              </thead>
              <tbody>
                {pendingCheckins.map((item, i) => (
                  <tr key={i}>
                    <td>{item.email}</td>
                    <td>{item.date}</td>
                    <td>{item.checkin_time || '—'}</td>
                    <td>
                      <button className="approve-btn" onClick={() => handleCheckinDecision(item._id, 'Accepted')}>Accept</button>
                      <button className="reject-btn" onClick={() => handleCheckinDecision(item._id, 'Rejected')}>Reject</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === 'leave' && (
          <>
            <div className="summary-cards">
              <div className="card total">Total Requests: {leaveRequests.length}</div>
              <div className="card pending">Pending: {leaveRequests.filter(l => l.status === 'Pending').length}</div>
              <div className="card accepted">Accepted: {leaveRequests.filter(l => l.status === 'Accepted').length}</div>
              <div className="card rejected">Rejected: {leaveRequests.filter(l => l.status === 'Rejected').length}</div>
            </div>
            <table>
              <thead><tr><th>Email</th><th>Date</th><th>Reason</th><th>Status</th><th>Actions</th></tr></thead>
              <tbody>
                {leaveRequests.map((leave, i) => (
                  <tr key={i}>
                    <td>{leave.email}</td>
                    <td>{leave.date}</td>
                    <td>{leave.reason}</td>
                    <td>{leave.status}</td>
                    <td>
                      {leave.status === 'Pending' ? (
                        <>
                          <button onClick={() => handleLeaveDecision(leave._id, 'Accepted')}>Accept</button>
                          <button onClick={() => handleLeaveDecision(leave._id, 'Rejected')}>Reject</button>
                        </>
                      ) : '—'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {activeTab === 'upload' && (
          <div className="upload-attendance">
            <h3>Upload Attendance CSV</h3>
            <input type="file" ref={fileInputRef} accept=".csv" />
            <button onClick={handleUploadAttendance}>Upload</button>
          </div>
        )}
{activeTab === 'add' && (
  <div className="add-employee">
    <h3>Add New Employee</h3>
    
    <input
      type="text"
      placeholder="Full Name"
      value={newEmployee.name}
      onChange={(e) => {
        const value = e.target.value;
        if (/^[a-zA-Z\s]*$/.test(value)) {
          setNewEmployee({ ...newEmployee, name: value });
        }
      }}
    />
    
    <input
      type="email"
      placeholder="Email"
      value={newEmployee.email}
      onChange={(e) => setNewEmployee({ ...newEmployee, email: e.target.value })}
    />
    
        <label style={{ fontSize: "14px", marginBottom: "5px", display: "block", color: "#555" }}>
      Date of Joining
    </label>
    <input
      type="date"
      value={newEmployee.doj}
      onChange={(e) => setNewEmployee({ ...newEmployee, doj: e.target.value })}
    />


    <input
      type="password"
      placeholder="Password"
      value={newEmployee.password}
      onChange={(e) => setNewEmployee({ ...newEmployee, password: e.target.value })}
    />

    <input
      type="text"
      placeholder="Department"
      value={newEmployee.department}
      onChange={(e) => {
        const value = e.target.value;
        if (/^[a-zA-Z\s]*$/.test(value)) {
          setNewEmployee({ ...newEmployee, department: value });
        }
      }}
    />

    <input
      type="text"
      placeholder="Position"
      value={newEmployee.position}
      onChange={(e) => {
        const value = e.target.value;
        if (/^[a-zA-Z\s]*$/.test(value)) {
          setNewEmployee({ ...newEmployee, position: value });
        }
      }}
    />

    <button
      onClick={() => {
        // Validation
        const { name, email, doj, password, department, position } = newEmployee;
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        if (!name || !email || !doj || !password || !department || !position) {
          toast.error("Please fill in all fields.");
          return;
        }

        if (!emailRegex.test(email)) {
          toast.error("Enter a valid email address.");
          return;
        }

        if (password.length < 8) {
          toast.error("Password must be at least 8 characters.");
          return;
        }

        // Call backend
        handleAddEmployee();

        // Reset form
        setNewEmployee({
          name: '',
          email: '',
          doj: '',
          password: '',
          department: '',
          position: ''
        });
      }}
    >
      Add Employee
    </button>
  </div>
)}

      </div>
    </div>
  );
};

export default AdminDashboard;
