// Final EmployeeDashboard.js - Includes Summary Cards Inside DashboardTab

import React, { useEffect, useState } from 'react';
import {
  checkin, checkout, getHistory,
  getLeaveHistory, submitLeaveRequest,
  getEmployeeSummary, getProfile,
  updateEmployeeProfile
} from '../services/api';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './EmployeeDashboard.css';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const Sidebar = ({ activeTab, setActiveTab, handleLogout }) => (
  <aside className="sidebar">
    <div className="sidebar-logo">Employee</div>
    <ul>
      <li className={activeTab === 'dashboard' ? 'active' : ''} onClick={() => setActiveTab('dashboard')}>Dashboard</li>
      <li className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}>Profile</li>
      <li className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</li>
      <li className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Request</li>
      <li className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</li>
      <li className={activeTab === 'history' ? 'active' : ''} onClick={() => setActiveTab('history')}>Attendance History</li>
      <li onClick={handleLogout}>Logout</li>
    </ul>
  </aside>
);

const DashboardTab = ({ summary, leavesLeft, nextHoliday }) => {
  const data = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        label: 'Attendance',
        data: [85, 15],
        backgroundColor: ['#4caf50', '#f44336'],
        borderWidth: 0
      }
    ]
  };
  return (
    <>
      <div className="card">
        <h3>Employee Activity</h3>
        <div className="small-chart">
          <Pie data={data} />
        </div>
      </div>
      <div className="summary-cards">
        <div className="card"><h4>Upcoming Holiday</h4><p>{nextHoliday?.name || 'â€”'}</p><small>{nextHoliday?.date || ''}</small></div>
        <div className="card"><h4>Leaves Taken</h4><p>{summary.leavesTaken}</p></div>
        <div className="card"><h4>Leaves Left</h4><p>{leavesLeft}</p></div>
        <div className="card"><h4>Pending Requests</h4><p>{summary.pendingRequests}</p></div>
      </div>
    </>
  );
};

// [AttendanceTab, LeaveTab, HistoryTab, HolidayTab, ProfileTab remain unchanged]
// Scroll to bottom for changes in usage below

const EmployeeDashboard = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [summary, setSummary] = useState({ leavesTaken: 0, pendingRequests: 0 });
  const [employee, setEmployee] = useState({ name: '', email: '', position: '', department: '', doj: '', bloodGroup: '' });
  const [editMode, setEditMode] = useState(false);

  const navigate = useNavigate();
  const leavesLeft = 20 - summary.leavesTaken;
  const holidays = [
    { name: 'May Day', date: '2025-05-01' },
    { name: 'Diwali', date: '2025-10-20' }
  ];
  const today = new Date();
  const nextHoliday = holidays.find(h => new Date(h.date) >= today);

  useEffect(() => {
    getProfile().then(res => setEmployee(res.data)).catch(() => toast.error('Failed to load profile'));
    getEmployeeSummary().then(res => setSummary(res.data)).catch(() => toast.error('Failed to load dashboard data'));
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  const handleProfileSave = async (e) => {
    e.preventDefault();
    const form = e.target;
    const updated = {
      name: form.name.value,
      email: form.email.value,
      position: form.position.value,
      department: form.department.value,
      doj: form.doj.value,
      bloodGroup: form.bloodGroup.value,
    };
    try {
      await updateEmployeeProfile(updated);
      setEmployee(updated);
      setEditMode(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error('Update failed');
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} handleLogout={handleLogout} />
      <main className="main-content">
        <h1>Employee Dashboard</h1>
        <p className="welcome-message">Hey, welcome back!</p>
        <div className="tabs">
          <button className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}>Profile</button>
          <button className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</button>
          <button className={activeTab === 'history' ? 'active' : ''} onClick={() => setActiveTab('history')}>Attendance History</button>
          <button className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Request</button>
          <button className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</button>
        </div>
        {activeTab === 'dashboard' && <DashboardTab summary={summary} leavesLeft={leavesLeft} nextHoliday={nextHoliday} />}
        {activeTab === 'profile' && <ProfileTab employee={employee} setEditMode={setEditMode} editMode={editMode} onSave={handleProfileSave} />}
        {activeTab === 'attendance' && <AttendanceTab />}
        {activeTab === 'history' && <HistoryTab />}
        {activeTab === 'leave' && <LeaveTab />}
        {activeTab === 'holiday' && <HolidayTab />}
        <ToastContainer />
      </main>
    </div>
  );
};

export default EmployeeDashboard;
