import React, { useState } from 'react';
import Sidebar from '../components/Sidebar';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [activeTab, setActiveTab] = useState('dashboard');

  const handleTabChange = (tab) => {
    if (tab === 'logout') {
      localStorage.clear();
      window.location.href = '/';
    } else {
      setActiveTab(tab);
    }
  };

  const dummyData = [
    { name: 'John Doe', function: 'Developer', status: 'ONLINE', date: '2025-05-01' },
    { name: 'Jane Smith', function: 'Manager', status: 'OFFLINE', date: '2025-04-20' },
  ];

  return (
    <div className="dashboard-container">
      <Sidebar activeTab={activeTab} onTabChange={handleTabChange} />
      <div className="main-panel">
        <div className="header">
          <h1>{activeTab.charAt(0).toUpperCase() + activeTab.slice(1)}</h1>
          <input type="text" placeholder="Search here" />
        </div>

        <div className="card">
          <div className="card-title">Employees</div>
          <table className="table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Function</th>
                <th>Status</th>
                <th>Employed</th>
              </tr>
            </thead>
            <tbody>
              {dummyData.map((emp, index) => (
                <tr key={index}>
                  <td>{emp.name}</td>
                  <td>{emp.function}</td>
                  <td>
                    <span className={`badge ${emp.status === 'ONLINE' ? 'green' : 'red'}`}>{emp.status}</span>
                  </td>
                  <td>{emp.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
